import os
import time

def get_size(path):
    size = os.path.getsize(path)
    if size < 1024:
        return f"{size} bytes"
    elif size < 1024*1024:
        return f"{round(size/1024, 2)} KB"
    elif size < 1024*1024*1024:
        return f"{round(size/(1024*1024), 2)} MB"
    elif size < 1024*1024*1024*1024:
        return f"{round(size/(1024*1024*1024), 2)} GB"

curr_path = os.getcwd()
dir_list = os.listdir(curr_path)
files = list()
files_size = list()
files_mtime = list()
folders = list()
for item in dir_list:
    if os.path.isdir(os.path.join(curr_path,item)):
        folders.append(item)
for item in dir_list:
    if os.path.isfile(os.path.join(curr_path,item)):
        files.append(item)
        # file_size = os.path.getsize(os.path.join(curr_path,item))
        file_size = get_size(os.path.join(curr_path,item))
        file_mtime = time.ctime(os.path.getmtime(os.path.join(curr_path,item)))
        files_mtime.append(file_mtime)
        files_size.append(file_size)

